(6, 37, '_give_sequoia_form_template_settings', 'a:5:{s:12:\"introduction\";a:6:{s:7:\"enabled\";s:7:\"enabled\";s:8:\"headline\";s:17:\"Support Our Cause\";s:11:\"description\";s:104:\"Help our organization by donating today! All donations go directly to making a difference for our cause.\";s:5:\"image\";s:118:\"http://localhost/new-imboxo/wp-content/plugins/give/assets/dist/images/onboarding-preview-form-image.min.jpg\";s:13:\"primary_color\";s:7:\"#4fa651\";s:12:\"donate_label\";s:10:\"Donate Now\";}s:14:\"payment_amount\";a:3:{s:12:\"header_label\";s:13:\"Choose Amount\";s:7:\"content\";s:135:\"How much would you like to donate? As a contributor to Imboxo Landing we make sure your donation goes directly to supporting our cause.\";s:10:\"next_label\";s:8:\"Continue\";}s:17:\"visual_appearance\";a:3:{s:16:\"decimals_enabled\";s:8:\"disabled\";s:13:\"primary_color\";s:7:\"#28C77B\";s:12:\"google-fonts\";s:7:\"enabled\";}s:19:\"payment_information\";a:7:{s:12:\"header_label\";s:20:\"Add Your Information\";s:8:\"headline\";s:19:\"Who\'s giving today?\";s:11:\"description\";s:49:\"We’ll never share this information with anyone.\";s:24:\"donation_summary_enabled\";s:7:\"enabled\";s:24:\"donation_summary_heading\";s:35:\"Here\'s what you\'re about to donate:\";s:25:\"donation_summary_location\";s:32:\"give_donation_form_before_submit\";i:0;a:7:{s:2:\"id\";s:14:\"checkout_label\";s:4:\"name\";s:13:\"Submit Button\";s:4:\"desc\";s:43:\"The button label for completing a donation.\";s:4:\"type\";s:11:\"text_medium\";s:10:\"attributes\";a:1:{s:11:\"placeholder\";s:10:\"Donate Now\";}s:7:\"default\";s:10:\"Donate Now\";s:18:\"mapToLegacySetting\";s:20:\"_give_checkout_label\";}}s:9:\"thank-you\";a:6:{s:5:\"image\";s:0:\"\";s:8:\"headline\";s:22:\"A great big thank you!\";s:11:\"description\";s:143:\"{name}, your contribution means a lot and will be put to good use in making a difference. We’ve sent your donation receipt to {donor_email}. \";s:7:\"sharing\";s:7:\"enabled\";s:19:\"sharing_instruction\";s:77:\"Help spread the word by sharing your support with your friends and followers!\";s:15:\"twitter_message\";s:38:\"I just gave to this cause. Who\'s next?\";}}'),
