(573, 12, 'home_explore_section_main_heading', 'Let Great Movies find you.'),
(574, 12, '_home_explore_section_main_heading', 'field_65aa1d6cd0ec6'),
(575, 95, 'barcode_image', '23'),
(576, 95, '_barcode_image', 'field_65a643aeb0546'),
(577, 95, 'home_first_title', 'Shop N Watch Movies Online With#Internet Movie Box Office'),
(578, 95, '_home_first_title', 'field_65aa00261417d'),
(579, 95, 'home_sub_title', 'Watch Movies, TV Shows, Series, and More'),
(580, 95, '_home_sub_title', 'field_65aa003c1417e'),
(581, 95, 'home_text_content', '<p>\r\n                        Imboxo lets you stream movies from creators you love while you discover unique products curated by passionate fans. Shop and watch, and get unlimited rewards . We don\'t just entertain, we empower – artists get rewarded on every play, and shoppers earn exclusive benefits.\r\n                        </p>\r\n                        <p>\r\n                            Join us in building global movie markets that fuel dreams, yours and your audience\'s. Get entertained, get Imboxo!\r\n                        </p>'),
(582, 95, '_home_text_content', 'field_65aa00511417f'),
(583, 95, 'home_contact_title_first_part', 'Join Our List, For'),
(584, 95, '_home_contact_title_first_part', 'field_65aa026714181'),
(585, 95, 'home_contact_title_second_part', 'Early Bird</span> Access'),
(586, 95, '_home_contact_title_second_part', 'field_65aa029014182'),
(587, 95, 'home_join_the_cause_level', 'Join The Cause'),
(588, 95, '_home_join_the_cause_level', 'field_65aa02e4ba4a0'),
(589, 95, 'home_about_first_title', 'Hello IMBOXO CREATORS and USERS,'),
(590, 95, '_home_about_first_title', 'field_65aa04216de79'),
(591, 95, 'home_about_big_text', 'Originally my idea for Internet Movie Box Office was to help movies find great audiences easily and for Audiences to find great movies. <br><br>\r\n                    Wether you are an IMBOXO CREATOR OR A USER or both, I want to personally welcome you to the begining of this transparent journey where literally MOVIE CREATORS and USERS can discover each other without the standard frustrations associated with making movies and trying to find great movies to watch.<br><br>\r\n                    \r\n                    Please keep in mind IMBOXO is just now starting to reach out to CREATORS to let them know of the IMBOXO service.  As Creators begin to learn of the service they will upload their movies their way to their own IMBOXO BOX OFFICES which you can follow and purchase just the movies you want to watch.<br><br>\r\n                    \r\n                    IMBOXO really is a whole new way for Creators and Users to interact and I am excited for all of us.<br><br>\r\n                    Sincerely<br><br>'),
(592, 95, '_home_about_big_text', 'field_65aa04446de7a'),
(593, 95, 'creator_name', 'Roger Roth'),
(594, 95, '_creator_name', 'field_65aa04bb6de7b'),
(595, 95, 'creator_designation', 'Creator of IMBOXO'),
(596, 95, '_creator_designation', 'field_65aa04dc6de7c'),
(597, 95, 'about_section_first_image', '86'),
(598, 95, '_about_section_first_image', 'field_65aa058616118'),
(599, 95, 'about_section_second_image', '87'),
(600, 95, '_about_section_second_image', 'field_65aa059f16119'),
(601, 95, 'creator_section_first_title', 'Your Personal Box Office, Anywhere, Anytime'),
(602, 95, '_creator_section_first_title', 'field_65aa1ab933c87'),
(603, 95, 'creator_section_sub_title', 'Connect With Audiences Worldwide'),
(604, 95, '_creator_section_sub_title', 'field_65aa1ad433c88'),
(605, 95, 'creator_section_big_text_content', 'As an experienced filmmaker who knows the challenges creators face to get their movies listed, marketed and seen by paying audiences is nothing short of a miracle every time.  Fortunately IMBOXO eliminates all the confusion and most of the expense that Creators and Users need to find each other.'),
(606, 95, '_creator_section_big_text_content', 'field_65aa1af233c89'),
(607, 95, 'creator_section_appeal_to_filmmakers_and_producers', 'Filmmakers and producers, do you want to list your movie?'),
