(672, 99, '_barcode_image', 'field_65a643aeb0546'),
(673, 99, 'home_first_title', 'Shop N Watch Movies Online With#Internet Movie Box Office'),
(674, 99, '_home_first_title', 'field_65aa00261417d'),
(675, 99, 'home_sub_title', 'Watch Movies, TV Shows, Series, and More'),
(676, 99, '_home_sub_title', 'field_65aa003c1417e'),
(677, 99, 'home_text_content', '<p>\r\n                        Imboxo lets you stream movies from creators you love while you discover unique products curated by passionate fans. Shop and watch, and get unlimited rewards . We don\'t just entertain, we empower – artists get rewarded on every play, and shoppers earn exclusive benefits.\r\n                        </p>\r\n                        <p>\r\n                            Join us in building global movie markets that fuel dreams, yours and your audience\'s. Get entertained, get Imboxo!\r\n                        </p>'),
(678, 99, '_home_text_content', 'field_65aa00511417f'),
(679, 99, 'home_contact_title_first_part', 'Join Our List, For'),
(680, 99, '_home_contact_title_first_part', 'field_65aa026714181'),
(681, 99, 'home_contact_title_second_part', 'Early Bird</span> Access'),
(682, 99, '_home_contact_title_second_part', 'field_65aa029014182'),
(683, 99, 'home_join_the_cause_level', 'Join The Cause'),
(684, 99, '_home_join_the_cause_level', 'field_65aa02e4ba4a0'),
(685, 99, 'home_about_first_title', 'Hello IMBOXO CREATORS and USERS,'),
(686, 99, '_home_about_first_title', 'field_65aa04216de79'),
(687, 99, 'home_about_big_text', 'Originally my idea for Internet Movie Box Office was to help movies find great audiences easily and for Audiences to find great movies. <br><br>\r\n                    Wether you are an IMBOXO CREATOR OR A USER or both, I want to personally welcome you to the begining of this transparent journey where literally MOVIE CREATORS and USERS can discover each other without the standard frustrations associated with making movies and trying to find great movies to watch.<br><br>\r\n                    \r\n                    Please keep in mind IMBOXO is just now starting to reach out to CREATORS to let them know of the IMBOXO service.  As Creators begin to learn of the service they will upload their movies their way to their own IMBOXO BOX OFFICES which you can follow and purchase just the movies you want to watch.<br><br>\r\n                    \r\n                    IMBOXO really is a whole new way for Creators and Users to interact and I am excited for all of us.<br><br>\r\n                    Sincerely<br><br>'),
(688, 99, '_home_about_big_text', 'field_65aa04446de7a'),
(689, 99, 'creator_name', 'Roger Roth'),
(690, 99, '_creator_name', 'field_65aa04bb6de7b'),
(691, 99, 'creator_designation', 'Creator of IMBOXO'),
(692, 99, '_creator_designation', 'field_65aa04dc6de7c'),
(693, 99, 'about_section_first_image', '86'),
(694, 99, '_about_section_first_image', 'field_65aa058616118'),
(695, 99, 'about_section_second_image', '87'),
(696, 99, '_about_section_second_image', 'field_65aa059f16119'),
(697, 99, 'creator_section_first_title', 'Your Personal Box Office, Anywhere, Anytime'),
(698, 99, '_creator_section_first_title', 'field_65aa1ab933c87'),
(699, 99, 'creator_section_sub_title', 'Connect With Audiences Worldwide'),
(700, 99, '_creator_section_sub_title', 'field_65aa1ad433c88'),
(701, 99, 'creator_section_big_text_content', 'As an experienced filmmaker who knows the challenges creators face to get their movies listed, marketed and seen by paying audiences is nothing short of a miracle every time.  Fortunately IMBOXO eliminates all the confusion and most of the expense that Creators and Users need to find each other.'),
(702, 99, '_creator_section_big_text_content', 'field_65aa1af233c89'),
(703, 99, 'creator_section_appeal_to_filmmakers_and_producers', 'Filmmakers and producers, do you want to list your movie?'),
