INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(1108, 123, 'home_text_content', '<p>\r\n                        Imboxo lets you stream movies from creators you love while you discover unique products curated by passionate fans. Shop and watch, and get unlimited rewards . We don\'t just entertain, we empower – artists get rewarded on every play, and shoppers earn exclusive benefits.\r\n                        </p>\r\n                        <p>\r\n                            Join us in building global movie markets that fuel dreams, yours and your audience\'s. Get entertained, get Imboxo!\r\n                        </p>'),
(1109, 123, '_home_text_content', 'field_65aa00511417f'),
(1110, 123, 'home_contact_title_first_part', 'Join Our List, For'),
(1111, 123, '_home_contact_title_first_part', 'field_65aa026714181'),
(1112, 123, 'home_contact_title_second_part', 'Early Bird</span> Access'),
(1113, 123, '_home_contact_title_second_part', 'field_65aa029014182'),
(1114, 123, 'home_join_the_cause_level', 'Join The Cause'),
(1115, 123, '_home_join_the_cause_level', 'field_65aa02e4ba4a0'),
(1116, 123, 'home_about_first_title', 'Hello IMBOXO CREATORS and USERS,'),
(1117, 123, '_home_about_first_title', 'field_65aa04216de79'),
(1118, 123, 'home_about_big_text', 'Originally my idea for Internet Movie Box Office was to help movies find great audiences easily and for Audiences to find great movies. <br><br>\r\n                    Wether you are an IMBOXO CREATOR OR A USER or both, I want to personally welcome you to the begining of this transparent journey where literally MOVIE CREATORS and USERS can discover each other without the standard frustrations associated with making movies and trying to find great movies to watch.<br><br>\r\n                    \r\n                    Please keep in mind IMBOXO is just now starting to reach out to CREATORS to let them know of the IMBOXO service.  As Creators begin to learn of the service they will upload their movies their way to their own IMBOXO BOX OFFICES which you can follow and purchase just the movies you want to watch.<br><br>\r\n                    \r\n                    IMBOXO really is a whole new way for Creators and Users to interact and I am excited for all of us.<br><br>\r\n                    Sincerely<br><br>'),
(1119, 123, '_home_about_big_text', 'field_65aa04446de7a'),
(1120, 123, 'creator_name', 'Roger Roth'),
(1121, 123, '_creator_name', 'field_65aa04bb6de7b'),
(1122, 123, 'creator_designation', 'Creator of IMBOXO'),
(1123, 123, '_creator_designation', 'field_65aa04dc6de7c'),
(1124, 123, 'about_section_first_image', '86'),
(1125, 123, '_about_section_first_image', 'field_65aa058616118'),
(1126, 123, 'about_section_second_image', '87'),
(1127, 123, '_about_section_second_image', 'field_65aa059f16119'),
(1128, 123, 'creator_section_first_title', 'Your Personal Box Office, Anywhere, Anytime'),
(1129, 123, '_creator_section_first_title', 'field_65aa1ab933c87'),
(1130, 123, 'creator_section_sub_title', 'Connect With Audiences Worldwide'),
(1131, 123, '_creator_section_sub_title', 'field_65aa1ad433c88'),
(1132, 123, 'creator_section_big_text_content', 'As an experienced filmmaker who knows the challenges creators face to get their movies listed, marketed and seen by paying audiences is nothing short of a miracle every time.  Fortunately IMBOXO eliminates all the confusion and most of the expense that Creators and Users need to find each other.'),
(1133, 123, '_creator_section_big_text_content', 'field_65aa1af233c89'),
(1134, 123, 'creator_section_appeal_to_filmmakers_and_producers', 'Filmmakers and producers, do you want to list your movie?'),
(1135, 123, '_creator_section_appeal_to_filmmakers_and_producers', 'field_65aa1b9533c8a'),
(1136, 123, 'creator_join_the_cause_text', 'Join The Cause'),
(1137, 123, '_creator_join_the_cause_text', 'field_65aa1bc933c8b'),
(1138, 123, 'home_explore_section_main_heading', 'Let Great Movies find you.'),
(1139, 123, '_home_explore_section_main_heading', 'field_65aa1d6cd0ec6'),
(1140, 123, 'home_donate_first_title', 'Invest in Inspiration: Every Dollar Helps Create Content You Love'),
(1141, 123, '_home_donate_first_title', 'field_65aa250f7249f'),
(1142, 123, 'home_donate_big_text', 'Hey there, movie magic fans! <br><br>Ever wonder how those stories you love come to life? It\'s thanks to passionate filmmakers and creators, pouring their hearts (and wallets) into every frame. But the truth is, passion alone doesn\'t pay the bills.<br><br> That\'s where you come in! Your contribution, big or small, fuels their dreams and brings more amazing content your way. Every dollar fuels a flicker of inspiration, every share ignites a spark of support.<br> <br> <h3>So, join the movement! Donate now and keep the reel world spinning. Let\'s make magic together!</h3>'),
(1143, 123, '_home_donate_big_text', 'field_65aa255d724a0'),
(1144, 123, 'latest_faq_main_title', ' <div class=\"h1\">latest</div>\r\n                    <div class=\"h1\">FAQS</div>\r\n                    <div class=\"small\">I love my work and every project is my pride</div>'),
(1145, 123, '_latest_faq_main_title', 'field_65aa2a35dd9b8'),
(1146, 123, 'contact_main_heading', 'Keep in touch with Us'),
(1147, 123, '_contact_main_heading', 'field_65aa3743ead2f'),
(1148, 123, 'appeal_to_creators_and_watchers_to_sign_up', 'Hello Creators and Watchers!<br>\r\n                    Sign up to Keep up and support IMBOXO and It\'s brand new tools to keep Watchers and Creators Connected like never before!'),
(1149, 123, '_appeal_to_creators_and_watchers_to_sign_up', 'field_65aa3668730b8'),
(1150, 123, 'connect_with_us_heading', 'Connect to us today'),
(1151, 123, '_connect_with_us_heading', 'field_65aa36e4730b9'),
(1152, 123, 'join_the_cause_title', 'Join The Cause'),
(1153, 123, '_join_the_cause_title', 'field_65aa36fc730ba');
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(2, 1, '2024-01-12 06:51:27', '2024-01-12 06:51:27', '<!-- wp:paragraph -->\n<p>This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>...or something like this:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>As a new WordPress user, you should go to <a href=\"http://theinternetmovieboxoffice.com/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!</p>\n<!-- /wp:paragraph -->', 'Sample Page', '', 'trash', 'closed', 'open', '', 'sample-page__trashed', '', '', '2024-01-12 06:52:23', '2024-01-12 06:52:23', '', 0, 'http://theinternetmovieboxoffice.com/?page_id=2', 0, 'page', '', 0),
(3, 1, '2024-01-12 06:51:27', '2024-01-12 06:51:27', '<!-- wp:heading --><h2>Who we are</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>Our website address is: http://theinternetmovieboxoffice.com.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Comments</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>When visitors leave comments on the site we collect the data shown in the comments form, and also the visitor&#8217;s IP address and browser user agent string to help spam detection.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>An anonymized string created from your email address (also called a hash) may be provided to the Gravatar service to see if you are using it. The Gravatar service privacy policy is available here: https://automattic.com/privacy/. After approval of your comment, your profile picture is visible to the public in the context of your comment.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Media</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>If you upload images to the website, you should avoid uploading images with embedded location data (EXIF GPS) included. Visitors to the website can download and extract any location data from images on the website.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Cookies</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>If you leave a comment on our site you may opt-in to saving your name, email address and website in cookies. These are for your convenience so that you do not have to fill in your details again when you leave another comment. These cookies will last for one year.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>If you visit our login page, we will set a temporary cookie to determine if your browser accepts cookies. This cookie contains no personal data and is discarded when you close your browser.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>When you log in, we will also set up several cookies to save your login information and your screen display choices. Login cookies last for two days, and screen options cookies last for a year. If you select &quot;Remember Me&quot;, your login will persist for two weeks. If you log out of your account, the login cookies will be removed.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>If you edit or publish an article, an additional cookie will be saved in your browser. This cookie includes no personal data and simply indicates the post ID of the article you just edited. It expires after 1 day.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Embedded content from other websites</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>Articles on this site may include embedded content (e.g. videos, images, articles, etc.). Embedded content from other websites behaves in the exact same way as if the visitor has visited the other website.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>These websites may collect data about you, use cookies, embed additional third-party tracking, and monitor your interaction with that embedded content, including tracking your interaction with the embedded content if you have an account and are logged in to that website.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Who we share your data with</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>If you request a password reset, your IP address will be included in the reset email.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>How long we retain your data</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>If you leave a comment, the comment and its metadata are retained indefinitely. This is so we can recognize and approve any follow-up comments automatically instead of holding them in a moderation queue.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>For users that register on our website (if any), we also store the personal information they provide in their user profile. All users can see, edit, or delete their personal information at any time (except they cannot change their username). Website administrators can also see and edit that information.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>What rights you have over your data</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>If you have an account on this site, or have left comments, you can request to receive an exported file of the personal data we hold about you, including any data you have provided to us. You can also request that we erase any personal data we hold about you. This does not include any data we are obliged to keep for administrative, legal, or security purposes.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Where your data is sent</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>Visitor comments may be checked through an automated spam detection service.</p><!-- /wp:paragraph -->', 'Privacy Policy', '', 'publish', 'closed', 'closed', '', 'privacy-policy', '', '', '2024-01-12 06:52:31', '2024-01-12 06:52:31', '', 0, 'http://theinternetmovieboxoffice.com/?page_id=3', 0, 'page', '', 0),
